import ReactDOM from "react-dom";
// import "./index.css";

const App=()=>{
   const[name,newName]=useState("");
   const[fName,lName] =useState("");
   const[f1name,lasname]=useState();
   const [first,last]=useState();


const inputEven = (event) =>{
   
   newName(event.target.value)
}
const lastEvent=(levent)=> {
   lasname(levent.target.value);

}

const click=()=>{
   lName(name );
   last(f1name);
}
 
    return(
      <>
      <h1>hello {fName} {first}</h1>
      <input type="text" placeholder="enter  first name" onChange={inputEven} value={name}/><br/>
      <input type="text" placeholder="enter last name" onChange={lastEvent}  value={f1name}/>
      <button onClick={click}>click here üëç</button>
      </>
   )}
ReactDOM.render(
   <>
  <App />
   </>
 ,document.getElementById("root"));
