import React, { useState } from "react";
import ReactDOM from "react-dom";


const App=()=>{
  const[fullName,setFullName]=useState({
    fname:"",
    lname:"",
    email:"",
    phone:""
  });


  const inputEvent=(event)=>{
    console.log(event.target.value)
    const value=event.target.value;
    const name=event.target.name;

    setFullName((preValue)=>{
      if(name ==="fname"){
        return{
        fname:value,
        lname:preValue.lname,
        email:preValue.email,
        phone:preValue.phone
        }
      }
    else if(name ==="lname"){
      return{
      fname:preValue.fname,
      lname:value,
      }
    }

    })
  }
  const onsubmits=(event)=>{
    event.preventDefault();

  }

  return(
    <>
    <form onSubmit={onsubmits}>
    <h1>hello {fullName.fname} {fullName.lname}</h1>
    <input type="text" placeholder="enter first name" name="fname" onChange={inputEvent}
     value={fullName.fname}/><br/>
    <input type="text"  placeholder="enter second name" name="lname" onChange={inputEvent} 
    value={fullName.lname}/><br/>
    <input type="text" placeholder="enter your email" name="email" onChange={inputEvent} 
    value={fullName.email}  /><br/>
      <input type="number" placeholder="enter your phone" name="phone" onChange={inputEvent} 
    value={fullName.phone}  /><br/>

    <button onChange={onsubmits}>click here</button>
    </form>
    </>
  )
}


ReactDOM.render(
   <>
  <App />
   </>
 ,document.getElementById("root"));
