import React, { useState } from "react";
import ReactDOM from "react-dom";


const App=()=>{
  const[fullName,setFullName]=useState();

  const[fullName1,setFullName1]=useState();
 const InputEvent=(event)=>{
   console.log(event.target.value)
   setFullName(event.target.value)
 }
 const InputEvent1=(event)=>{
   setFullName1(event.currentTarget.value)
 }
  return(<>
   <div>
    <h1>hello {fullName} {fullName1}</h1>
    <input type="text" placeholder="enter first name" onChange={InputEvent} /><br/>
    <input type="text" placeholder="enter second name" onChange={InputEvent1} /><br/>
    <button>submit</button>


   </div>



  </>)
}



ReactDOM.render(
   <>
  <App />
   </>
 ,document.getElementById("root"));
