import React, { useState } from "react";
import ReactDom from "react-dom";

export const Contact=()=>{

    const[info, setInfo] =useState({
        fname:'',
        phone:''
    });
    const inputEvent=(event)=>{
        setInfo(event.target.value)
   }
    const Done=(event)=>{
     event.preventDefault();
     alert("form Submitted");
    }
   

    return(
        <>
            <div>
            <form >
            <h1>hello {click}</h1>
                <input type="text" placeholder="fullname" onChange={inputEvent} value={info.fname} /><br/><br/>
                <input type="number" placeholder="phone nmbr" onChange={inputEvent} value={info.phone}/><br/><br/>
                <button type="submit" onClick={Done}>submit</button>
                </form>
            </div>
        </>
    )
}

export default Contact;
