import React, { useState } from "react";
import ReactDom from "react-dom";
import contact from "../src/images/contact.jpg"
import "./index.css";

export const Contact=()=>{

    const[info, setInfo] =useState({
        fullname:'',
        phone:'',
        email:"",
        message:""
    });
    const inputEvent=(event)=>{
       const {name, value} =event.target;

       setInfo((preVal)=>{
           return {
           ...preVal,
           [name]:value,
           }
   })
}
    const Done=(event)=>{
   
     alert("form Submitted");
    }
    const formSubmit=(e)=>{
     e.preventDefault();
    }

    return(
        <>
       
        <div style={{ width:"600",height:"350"}}>
        <div style={{backgroundImage:`url(${contact})`, width:'100%', height:"320" }}>
        <pre style={{textAlign:"center",fontSize:"50px",marginBottom:"0px"}}>
        
        
       
        <br/> <br/> <br/> <br/> <br/>
       Contact us<br/>
       <br/> <br/> <br/> <br/>
        
        </pre>

      
          </div>
          
           
            
         
          
          
        </div>
 
            <div style={{backgroundColor:"black", itemAlign:"center" }}>
            <form onSubmit={formSubmit} >
            <h1>hello {info.fullname} </h1>
                <input type="text" placeholder="fullname" onChange={inputEvent}  name="fullname" value={info.fname} /><br/><br/>
                <input type="number" placeholder="phone nmbr" onChange={inputEvent} name="phone" value={info.phone}/><br/><br/>
                <input type="email" placeholder="email" onChange={inputEvent} name="email" value={info.email}/><br/><br/>
                <input type="message" placeholder="message" onChange={inputEvent} name="message" value={info.message}/><br/><br/>
                <button type="submit" onClick={Done}>Submit</button>
                </form>
            </div>
            
        </>
    )
}

export default Contact;
