import React, { useState } from "react";
import ReactDOM from "react-dom";


const App=()=>{
  const[fullName,setFullName]=useState({
    fname:"",
    lname:""
  });


 const InputEvent=(event)=>{
   
   const value =event.target.value;
   const name=event.target.name; 
  setFullName((preValue) =>{

  if(name==="fname"){
    return{
    fname:value,
    lname:preValue.lname};
  }
  else  if(name==="lname"){
    return{
    fname:preValue.fname,
    lname:value
  };}
  
  }
  )


  }
 const submits =(event)=>{
   event.preventDefault();
   alert("you are done")
 }
 
  return(<>
   <div>
   <form onSubmit={submits}>
    <h1>hello {fullName.fname} {fullName.lname}</h1>
    <h1 placeholder="mail"></h1>
    <input type="text" placeholder="enter first name" name="fname" onChange={InputEvent} 
      value= {fullName.fname}
    /><br/>
    <input type="text" placeholder="enter second name" name="lname" onChange={InputEvent}
     value= {fullName.lname} /><br/>
    <button onClick={submits}>submit</button>

    </form>
   </div>



  </>)
 }



ReactDOM.render(
   <>
  <App />
   </>
 ,document.getElementById("root"));
