import React, { useState } from "react";
import ReactDOM from "react-dom";


const App=()=>{
  const[fullName,setFullName]=useState({
    fname:"",
    lname:""
  });


  const inputEvent=(event)=>{
    console.log(event.target.value)
    value=event.target.value;
    name=event.target.name;
    setFullName((preValue)=>{
      if(name==="fname"){
        return <>
        fname:value,
        lname:preValue.lname,
        </>
      }
    else if(name==="lname"){
      return <>
      fname:preValue.lname,
      lname:value,
      </>
    }

    })
  }
  const onsubmits=(event)=>{
    event.preventDefault();

  }

  return(
    <>
    <form onSubmit={onsubmits}>
    <h1>hello {fullName.fname}{fullName.lname}</h1>
    <input type="text" placeholder="enter first name" name="fname" onChange={inputEvent} /><br/>
    <input type="text"  placeholder="enter second name" name="lname" onChange={inputEvent} /><br/>
    <button onChange={onsubmits}>click here</button>
    </form>
    </>
  )
}


ReactDOM.render(
   <>
  <App />
   </>
 ,document.getElementById("root"));
