import React, { useState } from "react";
import ReactDom from "react-dom";

export const Contact=()=>{

    const[Fname, setFname] =useState();
    const[phone, setphone] =useState();
    const Names=(event)=>{
         setFname(event.target.value)
    }
    const Phones=(event)=>{
        setphone(event.target.value)
   }
   const Done=(event)=>{
    setFname(event.target.value)
    setphone(event.target.value)
   }

    return(
        <>
            <div>
            <form>
            <h1>hello {Fname} with {phone}</h1>
                <input type="text" placeholder="fullname" onChange={Names} value={Fname} /><br/><br/>
                <input type="number" placeholder="phone nmbr" onChange={Phones} value={phone}/><br/><br/>
                <button type="submit" onClick={Done}>submit</button>
                </form>
            </div>
        </>
    )
}

export default Contact;
